import React, { Component } from 'react';

import curso from '../../../data/Pruebas';


class Filtro extends Component{

  constructor(props){
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }/* 
  <form onSubmit={this.handleSubmit}>
  <label>
    Name:
    <input type="text" value={this.state.value} onChange={this.handleChange} />
  </label>
  <input type="submit" value="Submit" />
`</form> */


  render() {

      return (
          <>  
          <div class="filtro">
              <h5 class="filtroText" >Filtro de cursos:</h5>
              <input type="text" value={this.state.value} onChange={this.handleChange}  title="filtro" placeholder="Filtrar cursos"/>
            </div>
          </>
      )
    }
}

const MyCarousel = props => (
  <div >
    <div className="App">
      <ul>

        {curso.filter(curso => curso.actualizacion != "20/10/21" && 
          curso.palsClavs.includes("programacion")==true).map(curso => (
        <option key={curso.imagen} value={curso.actualizacion} 
          value={curso.tutor}>
                {curso.actualizacion}, {curso.imagen},{curso.tutor}
          </option>))
        }
        {/* {curso.filter(curso => curso.actualizacion != "21/10/21").Pruebas.map((curso) => {
          return (
            <div id="esta" width="80px" height="600px">
                <li>
                  {curso.imagen}  {curso.actualizacion} {curso.tutor} 
                </li>
            </div>
          );
        })} */}
      </ul>
    </div>

  </div>
)

export default class Carrusel extends Component {

  render() {
    return (
        <main role="main">
          <Filtro />
          <MyCarousel />
        </main>
    );
  }
}



